import Input from '../Input'
import styled from 'styled-components'
import { useState } from 'react'
import { livros } from './dadosPesquisa'

const PesquisaContainer = styled.section`
        background-image: #8CB9BD;
        color: #FEFBF6;
        text-align: center;
        padding: 85px 0;
        height: 270px;
        width: 100%;
`

const Titulo = styled.h2`
        color: #FEFBF6;
        font-size: 36px;
        text-align: center;
        width: 100%;
`

const Subtitulo = styled.h3`
        font-size: 16px;
        font-weight: 500;
        margin-bottom: 40px;
`

function Pesquisa () {
    const [livrosPesquisados, setLivrosPesquisados] = useState('')
    return (
        <PesquisaContainer>
            <Titulo>Já sabe por onde começar?</Titulo>
            <Subtitulo>Encontre em nossa estante o que precisa para o seu desenvolvimento.</Subtitulo>
            <Input
                placeholder="Qual é sua próxima leitura?"
                onBlur={evento => setTextoDigitado(evento.target.value)}
            />
        </PesquisaContainer>   
    )
}

export default Pesquisa
